import React from 'react'

import useForm from './useForm'
import validate from './SignInFormValidationRules'

const axios = require('axios');

function SignIn() {

    const { values, errors, handleSubmit, handleChange} = useForm(signup, validate);

    function signup() {
        console.log(values);
        axios.post('/create', values)
        .then(res => {
            setError(false);
            localStorage.setItem('token', res.data.token);
        })
        .catch(err => console.log(err));
    }

    return (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <label htmlFor="SignInEmail">Email address</label>
                <input type="email" name="email" className={`form-control form-control-sm ${errors.email && "is-invalid"}`} id="SignInEmail" value={values.email || ""} onChange={handleChange} required/>
                {errors.email && (<small id="SignInEmailHelp" className="form-text text-danger">{errors.email}</small>)}
            </div>
            <div className="form-group">
                <label htmlFor="SignInPassword">Password</label>
                <input type="password" name="password" className={`form-control form-control-sm ${errors.password && "is-invalid"}`} id="SignInPassword" value={values.password || ""} onChange={handleChange} required/>
                {errors.password && (<small id="SignInPasswordHelp" className="form-text text-danger">{errors.password}</small>)}
            </div>
            <button type="submit" className="btn btn-success">Sign In</button>
        </form>
    );
}

export default SignIn;
