import React, { useEffect, useState } from 'react'

import useForm from './useForm'
import validate from './SignUpFormValidationRules'

const axios = require('axios');

function SignUp() {

    const { values, errors, handleSubmit, handleChange} = useForm(signup, validate);
    const [location, setLocation] = useState({
        country: "",
        state: ""
    });

    useEffect(() => {

        axios.get('https://freegeoip.app/json/')
        .then(res => console.log(res.data.country_name))
        .catch(err => console.log(err));

        /* async function fetchLocation() {
            const res = await fetch("https://freegeoip.app/json/");
            res
            .json()
            .then(res => setLocation({...location, country: res.country_name, state: res.region_name}))
            .catch(err => console.log(err));
        }

        fetchLocation(); */
    });

    function signup() {
        console.log({...values, country: location.country, state: location.state});
    }

    return (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <label htmlFor="Name">Full Name</label>
                <input type="text" name="name" className={`form-control form-control-sm ${errors.name && "is-invalid"}`} id="Name" value={values.name || ""} onChange={handleChange} required/>
                {errors.name && (<small id="NameError" className="form-text text-danger">{errors.name}</small>)}
            </div>
            <div className="form-group">
                <label htmlFor="SignUpEmail">Email address</label>
                <input type="email" name="email" className={`form-control form-control-sm ${errors.email && "is-invalid"}`} id="SignUpEmail" value={values.email || ""} onChange={handleChange} required/>
                {errors.email && (<small id="SignUpEmailError" className="form-text text-danger">{errors.email}</small>)}
            </div>
            <div className="form-group">
                <label htmlFor="Username">Username</label>
                <input type="text" name="username" className={`form-control form-control-sm ${errors.username && "is-invalid"}`} id="Username" value={values.username || ""} onChange={handleChange} required/>
                {errors.username && (<small id="UsernameError" className="form-text text-danger">{errors.username}</small>)}
            </div>
            <div className="form-group">
                <label htmlFor="SignUpPassword">Password</label>
                <input type="password" name="password" className={`form-control form-control-sm ${errors.password && "is-invalid"}`} id="SignUpPassword" value={values.password || ""} onChange={handleChange} required/>
                {errors.password && (<small id="SignUpPasswordError" className="form-text text-danger">{errors.password}</small>)}
            </div>
            <div className="form-group">
                <label htmlFor="DOB">Date of Birth</label>
                <input type="date" name="dob" className={`form-control form-control-sm ${errors.dob && "is-invalid"}`} id="DOB" value={values.dob || ""} onChange={handleChange} required/>
                {errors.dob && (<small id="DateofBirthError" className="form-text text-danger">{errors.dob}</small>)}
            </div>
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputCountry">Country</label>
                    <input type="text" name="country" className="form-control form-control-sm" id="inputCountry" value={location.country || ""} onChange={handleChange} required/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputState">State</label>
                    <input type="text" name="state" className="form-control form-control-sm" id="inputState" value={location.state || ""} onChange={handleChange} required/>
                </div>
            </div>
            <div className="form-group">
                <label className="mr-3">Gender</label>
                <div className="form-check form-check-inline">
                    <input className="form-check-input" type="radio" name="gender" id="male" value="Male" onChange={handleChange}/>
                    <label className="form-check-label" htmlFor="male">Male</label>
                </div>
                <div className="form-check form-check-inline">
                    <input className="form-check-input" type="radio" name="gender" id="female" value="Female" onChange={handleChange}/>
                    <label className="form-check-label" htmlFor="female">Female</label>
                </div>
                {errors.gender && (<small id="GenderError" className="form-text text-danger">{errors.gender}</small>)}
            </div>
            <button type="submit" className="btn btn-success">Sign Up</button>
        </form>
    );
}

export default SignUp;
